import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ShopContext } from './shop-context';
import { SingleProduct } from './singleProduct';
import './navBar.css'; // Импортируем стили таббара

const Cart = () => {
  const { cartItems, getTotalCartAmount, addToCart, removeFromCart } = useContext(ShopContext);

  return (
    <div className='cart-page'>
      <div className='navbar'>
        <div className='links'>
          <Link to='/home'>Home</Link>
          <Link to='/cart'>Cart</Link>
          <Link to='/profile'>Profile</Link>
        </div>
      </div>
      <div className='cart'>
        <h2>Shopping Cart</h2>
        {Object.entries(cartItems).map(([productId, quantity]) => (
          <SingleProduct key={productId} data={{ id: productId, quantity }} />
        ))}
        <h3>Total Amount: ${getTotalCartAmount()}</h3>
      </div>
    </div>
  );
};

export default Cart;
