// SingleProduct.js
import React, { useContext } from 'react';
import { ShopContext } from './shop-context';

const SingleProduct = ({ data }) => {
  const { id, name, price, imageName } = data;
  const { addToCart, cartItems } = useContext(ShopContext);

  const handleAddToCart = () => {
    addToCart(id);
  };

    const imageSrc = (() => {
    switch (imageName) {
        case 'airpods':
            return airpods;
        case 'appleWatch':
            return appleWatch;
        case 'homepod':
            return homepod;
        case 'imac24':
            return imac24;
        case 'ipadpro11':
            return ipadpro11;
        case 'iphone15':
            return iphone15;
        case 'macbook':
            return macbook;
        case 'visionPro':
            return visionPro;
        default:
            return null;
    }
})();

  return (
    <div className="product">
      <img src={imageSrc} alt={name} />
      <div className="description">
        <p><b>{name}</b></p>
        <p><b>${price}</b></p>
      </div>
      {cartItems[id] ? (
        <div>Quantity: {cartItems[id]}</div>
      ) : (
        <button className="addToCartBttn" onClick={handleAddToCart}>Add to Cart</button>
      )}
    </div>
  );
};

export default SingleProduct;
