import React from 'react';
import airpods from "./assets/airpods.png";
import appleWatch from "./assets/appleWatch.png";
import homepod from "./assets/homepod.png";
import imac24 from "./assets/imac24.png";
import ipadpro11 from "./assets/ipadpro11.png";
import iphone15 from "./assets/iphone15.png";
import macbook from "./assets/macbook.png";
import visionPro from "./assets/visionPro.png";

const productObject = {
    "iphone15": iphone15,
    "macbook": macbook,
    "homepod":homepod,
    "imac24": imac24,
    "airpods": airpods,
    "appleWatch": appleWatch,
    "ipadpro11": ipadpro11,
    "visionPro": visionPro
}

export const SingleProduct = (props) => {
    const { name, price, imageName } = props.data;
    return (
        <div className='product'>
            <img src={productObject.imageName} />
            <div className='description'>
                <p><b>{name}</b></p>
                <p><b>${price}</b></p>
            </div>
            <button className='addToCartBttn'>Add to Cart</button> {/* Кнопка добавления в корзину */}
        </div>
    );
};
