// Cart.js
import React, { useContext } from 'react';
import { ShopContext } from './shop-context';

const Cart = () => {
  const { cartItems, getTotalCartAmount, addToCart, removeFromCart } = useContext(ShopContext);

  return (
    <div>
      <h2>Shopping Cart</h2>
      {Object.entries(cartItems).map(([productId, quantity]) => (
        <div key={productId}>
          <p>Product ID: {productId}</p>
          <p>Quantity: {quantity}</p>
          <button onClick={() => addToCart(productId)}>Add One</button>
          <button onClick={() => removeFromCart(productId)}>Remove One</button>
        </div>
      ))}
      <h3>Total Amount: ${getTotalCartAmount()}</h3>
    </div>
  );
};

export default Cart;
